import { useState, createContext, useContext } from "react";
import {CalculatorContext,CalculatorProvider} from "../context/Calculator.js";


const Button = ({value})=>{
    const {info,setInfo} = useContext(CalculatorContext);

    const clearHadler=()=>{
        setInfo({a:0});
    }

    const dotHandler=()=>{
        setInfo({a:info.a+value});
    }
    
    const clickHandler= ()=>{
        // console.log(value);
        // setInfo({a:value+info.a});
        value == "." ? dotHandler() :
        value == "AC" ? clearHadler() : clearHadler() 
    }    

    return(
        <button onClick={clickHandler}className="button"> {value} </button>
    )
}

export default Button;
