import { useState, createContext, useContext } from "react";
import {CalculatorContext,CalculatorProvider} from "../context/Calculator.js";


const Button = ({value})=>{
    const {info,setInfo} = useContext(CalculatorContext);

    const clearHadler=()=>{
        setInfo({
            a:0,
            b:0,
            op:""
        });
    }

    const dotHandler=()=>{
        setInfo({a:info.a+value});
    }

    const numberHandler= ()=>{
        setInfo({a:Number(info.a+""+value)});
    }

    const opHandler= ()=>{
        setInfo({
            a:0,
            b:info.a,
            op:value,
        });
        
    }

    const equalHandler= ()=>{
        console.log(info.op);
        console.log("a",a.op);
        console.log("b",b.op);
        const res = 
            info.op != "+" ? info.a + info.b : 88;

        setInfo({
            a:res,
            b:0,
            op:""
        })    

    }
    
    const clickHandler= ()=>{
        // console.log(value);
        value == "." ? dotHandler() :
        value == "AC" ? clearHadler() :
        value == "=" ? equalHandler(): 
        value == "+" ? opHandler():
        value == "-" ? opHandler():
        value == "%" ? opHandler():
        value == "/" ? opHandler():
        value == "X" ? opHandler():
        value == "+/-" ? opHandler():
        numberHandler();
    }    

    return(
        <button onClick={clickHandler}className="button"> {value} </button>
    )
}

export default Button;
