import { useState, createContext, useContext } from "react";
import {CalculatorContext,CalculatorProvider} from "../context/Calculator.js";


const Button = ({value})=>{
    const {info,setInfo} = useContext(CalculatorContext);

    const clearHadler=()=>{
        setInfo({
            a:0,
            b:0,
            op:""
        });
    }

    const dotHandler=()=>{
        setInfo({
            a:info.a+value,
            b:info.b,
            op:info.op
        
        });
    }

    const numberHandler= ()=>{
        setInfo({
            a:Number(info.a+""+value),
            b:info.b,
            op:info.op
        });
    }

    const opHandler= ()=>{
        setInfo({
            a:0,
            b:info.a,
            op:value,
        });
        
    }

    const equalHandler= ()=>{
        const res = 
            info.op == "+" ? info.a + info.b :
            info.op == "-" ? info.a - info.b :
            info.op == "X" ? info.a * info.b :
            info.op == "/" ? info.a / info.b : 
            info.a;
        setInfo({
            a:res,
            b:0,
            op:""
        })    
    }

    const percentageHandler= ()=>{
        setInfo({
            a:info.a/100,
            b:info.b/100,
            op:""
        }) 
    }
    
    const clickHandler= ()=>{
        // console.log(value);
        value == "." ? dotHandler() :
        value == "AC" ? clearHadler() :
        value == "=" ? equalHandler():
        value == "%" ? percentageHandler(): 
        value == "+" ? opHandler():
        value == "-" ? opHandler():
        value == "/" ? opHandler():
        value == "X" ? opHandler():
        value == "+/-" ? opHandler():
        numberHandler();
    }    

    return(
        <button onClick={clickHandler}className="button"> {value} </button>
    )
}

export default Button;
